	public int cleanFromMissingFiles() {
		List list = new LinkedList();
		list.addAll(songs);
		Iterator it = list.iterator();
		while (it.hasNext()) {
			MP3Song song = (MP3Song) it.next();
			File f = new File(song.getPath());
			if (!f.exists())
				it.remove();
		}
		songs.clear();
		songs.addAll(list);
	}

everything is all right.

